import React, { useEffect, useState } from 'react'
import df from "./Data"
import { useNavigate } from 'react-router-dom';
import SearchBox from './SearchBox';
const First = () => {
   const nav=useNavigate();
    const [val,setval]=useState("");
    const [filttype,setfilttype]=useState(null);
    const [data,setdata]=useState([])
    //console.log("df : ",df);
  
    const Start=async()=>{
        const fr=await fetch("http://localhost:5000/start");
        const rp=await fr.json();
        setdata(rp);
        
        console.log("new one : ",rp);
    }
    const but1=()=>{
        
        setfilttype("author");
    }
    const but2=()=>{
        
        setfilttype("genre");
    }
    const but3=()=>{
        
        setfilttype("rating");
    }


    const Datafilter1=()=>{
        const u=data.filter((d)=>{
            const r=d.author;
           // console.log(val)
            const t=r.includes(val);
           // console.log(t)
            return t;
        });
        setdata(u)

    }
    const Datafilter2=()=>{
        const u=data.filter((d)=>{
            const r=d.genre;
            //console.log(val)
            const t=r.includes(val);
            //console.log(t)
            return t;
        });
        setdata(u)

    }
    const Datafilter3=()=>{
        const u=data.filter((d)=>{
            const r=d.rating;
            //console.log(val)
            const t=r>val;
            //console.log(t)
            return t;
        });
        setdata(u)

    }
    
    useEffect(()=>{
        if(val===""){
            //setdata(df);
            Start();
            
        console.log("lkkk :",data)
        }
        else if(filttype==="author"){
        Datafilter1();
        }
        else if(filttype==="genre"){
            Datafilter2();
            }
        else if(filttype==="rating"){
        Datafilter3();
        }
        console.log("df : ",data)

    },[filttype,val])

    const Tested=async(id)=>{
        console.log(id);
        nav(`/${id}`);

        //const sd1=await fetch(`http://localhost:5000/point/${id}`);

        


    }

  return (
    <div>
        <h1 className='text-lg font-medium text-justify items-center'>Audio-Books Catelogue</h1>
        <SearchBox className=' rounded-lg boder-2 w-3/4 border-black'  val={val} setval={setval}/>
        <div>
            <button onClick={but1}>Author</button>
            <button onClick={but2}>Genre</button>
            <button onClick={but3}>Rating</button>
        </div>
        <h2>{val}</h2>
        <div >
            {data.map((d)=>(
                <div key={d._id} onClick={()=>Tested(d._id)}>
                    <img src={d.cover} alt="" />
                    <h1>{d.title}</h1>
                    <h3>{d.author}</h3>
                    </div>
            ))}
        </div>

      
    </div>
  )
}

export default First
