import React, { useEffect, useState } from 'react'
import { useParams } from 'react-router-dom'
import Comment from '../Back/Comment';
const Second = () => {
    const [data,setdata]=useState(null);
    const [name,setname]=useState("");
    const [rate,setrate]=useState("");
    const [comment,setcomment]=useState("");
    const {id}=useParams();
    const up={id};
    const Initiate=async()=>{
        const t=52
        const rd=await fetch(`http://localhost:5000/point/${id}`);
        const y1= await rd.json();
        console.log(y1.audio);

        setdata(y1.audio);

        //const rd=await fetch(`http://localhost:5000/point/${id}`);
        
    }
    useEffect(()=>{
        Initiate();
       // console.log(data)
    })

    const Submithandler=async(e)=>{
        e.preventDefault();
        console.log({id}.id)
        const cmt={audio_id:{id}.id, name:name, rate:parseInt(rate),comment:comment};
        const prd=await fetch("http://localhost:5000/review",{
            method: 'POST',
            body: JSON.stringify(cmt),
            headers: {
              'Content-Type': 'application/json'
            },
        })
        const resp=await prd.text();
        console.log(resp);
        alert("Feedback Recieved");
    }
  return (
    <div>
        <h1>{id}</h1>
        {data==null?
        <h1>loading.....</h1>:
        <div>
            <div>
                
            <div>
            <img src={data.cover} alt="" />
        </div>
        <div>
            <h1>{data.title}</h1>
            <h2>{data.auther}</h2>
            <h2>{data.genre}</h2>
            <h3>{data.rating}</h3>
            <p>{data.desc}</p>
        </div>
            </div>
            <div>
              
            </div>
            <div>
         <h1>Feedback Form</h1>
         <div>
           
                <div>
                    <label htmlFor="">Name : </label>
                    <input type="text" value={name} onChange={(e)=>setname(e.target.value)}/>
                </div>
                <div>
                    <label htmlFor="">Rate your experience : </label>
                    <input type="text" value={rate} onChange={(e)=>setrate(e.target.value)}/>
                </div>
                <div>
                    <label htmlFor="">Any comments : </label>
                    <input type="text" value={comment} onChange={(e)=>setcomment(e.target.value)}/>
                </div>
                <button onClick={Submithandler}>Send</button>
           
         </div>
            </div>
            
            </div>}
      
    </div>
  )
}

export default Second
