import React, { useEffect, useState } from 'react'
import { useParams } from 'react-router-dom'
import Review from './Review';
import './Second.css'
const Second = () => {
    const [data,setdata]=useState(null);
    const [name,setname]=useState("");
    const [rate,setrate]=useState();
    const [comment,setcomment]=useState("");
    const [review,setreview]=useState([])
    const {id}=useParams();
    const up={id};
    const Initiate=async()=>{
        const t=52
        const rd=await fetch(`http://localhost:5000/point/${id}`);
        const y1= await rd.json();
        //console.log(y1.review);

        setdata(y1.audio);
        setreview(y1.review);


        //const rd=await fetch(`http://localhost:5000/point/${id}`);
        
    }
    useEffect(()=>{
        Initiate();
       // console.log(data)
    })

    const Submithandler=async(e)=>{
        e.preventDefault();
        console.log({id}.id)
        const cmt={audio_id:{id}.id, name:name, rate:parseInt(rate),comment:comment};
        const prd=await fetch("http://localhost:5000/review",{
            method: 'POST',
            body: JSON.stringify(cmt),
            headers: {
              'Content-Type': 'application/json'
            },
        })
        const resp=await prd.text();
        console.log(resp);
        alert("Feedback Recieved");
    }
    const handleChange = (e) => {
        const value = Number(e.target.value);
        if (value > 5) {
          setrate(0);
        } else {
          setrate(value);
        }
      };
    
  return (
    <div>
        {data==null?
        <h1>loading.....</h1>:
        <div>
            <div className='part2'>
                
            <div>
            <img src={data.cover} alt="" className='p2' />
            </div>
            <div className='p22'>
                <h1 className='k1'>{data.title}</h1>

                <h2 className='k2'>Author : <span className='k22'>{data.author}</span></h2>
                
                <p className='k25'> Description : </p>
                <span className='k5'>{data.desc}</span>
                <hr />
                <h2 className='k3'>Genre : {data.genre}</h2>
                <hr/>
                <h3 className='k4'>Rating : {data.rating} stars</h3>
            </div>
                </div>
            <div className='part3'>
            
           {review.length==0?<h1>No review.</h1>:<Review Data={review}/>} 
           
            </div>
            <div className='part5'>
         <h1 className='p3'>Add review/comments </h1>
         <div className='pp5'>
           
                <div className='p5'>
                    <label className="lab1"htmlFor="">Name : </label>
                    <input className='ing1' type="text" value={name} onChange={(e)=>setname(e.target.value)}/>
                </div>
                <div className='p5'>
                    <label className="lab1" htmlFor="">Rate your experience out of 5: </label>
                    <input className='ing1' type="number" min="0" max="5" value={rate} onChange={handleChange}/>
                </div>
                <div className='p5'>
                    <label className="lab1" htmlFor="">Any comments : </label>
                    <textarea className='ing1' type="text" value={comment} onChange={(e)=>setcomment(e.target.value)} rows="5" cols="20"/>
                </div>
                <button className='but2' onClick={Submithandler}>Send</button>
           
         </div>
            </div>
            
            </div>}
      
    </div>
  )
}

export default Second
