import React, { useEffect, useState } from 'react'
import { useParams } from 'react-router-dom'
import Review from './Review';
import './Second.css'
import Feedback from './Feedback';
const Second = () => {
    const [data,setdata]=useState(null);
    const [name,setname]=useState("");
    const [rate,setrate]=useState("");
    const [comment,setcomment]=useState("");
    const [review,setreview]=useState([])
    const {id}=useParams();
    const up={id};
    const Initiate=async()=>{
        const t=52
        const rd=await fetch(`http://localhost:5000/point/${id}`);
        const y1= await rd.json();
 
        setdata(y1.audio);
        setreview(y1.review);  
    }
    useEffect(()=>{
        Initiate();
       // console.log(data)
    })

    const Submithandler=async(e)=>{
        e.preventDefault();
        console.log({id}.id)
        const cmt={audio_id:{id}.id, name:name, rate:parseInt(rate),comment:comment};
        const prd=await fetch("http://localhost:5000/review",{
            method: 'POST',
            body: JSON.stringify(cmt),
            headers: {
              'Content-Type': 'application/json'
            },
        })
        const resp=await prd.text();
        console.log(resp);
        alert("Feedback Recieved");
    }
    
    
  return (
    <div>
        {data==null?
        <h1>loading.....</h1>:
        <div>
            <div className='part2'>
                
            <div>
            <img src={data.cover} alt="" className='p2' />
            </div>
            <div className='p22'>
                <h1 className='k1'>{data.title}</h1>

                <h2 className='k2'>Author : <span className='k22'>{data.author}</span></h2>
                
                <p className='k25'> Description : </p>
                <span className='k5'>{data.desc}</span>
                <hr />
                <h2 className='k3'>Genre : {data.genre}</h2>
                <hr/>
                <h3 className='k4'>Rating : {data.rating} stars</h3>
            </div>
                </div>
            <div className='part3'>
            
           {review.length==0?<h1>No review.</h1>:<Review Data={review}/>} 
           
            </div>
            <Feedback Gid={id}/>
            
            
            </div>}
      
    </div>
  )
}

export default Second
