import React, { useEffect, useState } from 'react'
import df from "./Data"
import SearchBox from './SearchBox';
const First = () => {
    const [val,setval]=useState("");
    const [filttype,setfilttype]=useState(null);
    const [data,setdata]=useState(df)
    console.log("df : ",df);
    const Tested=(e)=>{
        console.log(e.target.key)


    }
    const Datafilter=()=>{
        setfilttype("author");
        const u=data.filter((d)=>{
            const r=d.filttype;
            console.log(val)
            const t=r.includes(val);
            console.log(t)
            return t;
        });
        setdata(u)
        //console.log(u)


    }
    useEffect(()=>{
        Datafilter();

    },[filttype,val])

  return (
    <div>
        <h1>Audio-Books Catelogue</h1>
        <SearchBox  val={val} setval={setval}/>
        <div>
            <button onClick={Datafilter}>Author</button>
            <button></button>
            <button></button>
        </div>
        <h2>{val}</h2>
        <div >
            {data.map((d)=>(
                <div key={d.id} onClick={Tested}>
                    <img src={d.cover} alt="" />
                    <h1>{d.title}</h1>
                    <h3>{d.author}</h3>
                    </div>
            ))}
        </div>

      
    </div>
  )
}

export default First
