import { useParams } from 'react-router-dom'
const Second = () => {
    const [data,setdata]=useState(null);
    const {id}=useParams();
    const up={id};
    const Initiate=async()=>{
        const t=52
        const rd=await fetch(`http://localhost:5000/point/${id}`);
        const y1= await rd.json();
        console.log(y1);
        setdata(y1);
        
    }
    useEffect(()=>{
        Initiate();
       // console.log(data)
    })
  return (
    <div>
        <h1>{id}</h1>
        {data==null?
        <h1>loading.....</h1>:
        <div>
            <div>
            <img src={data.cover} alt="" />
        </div>
        <div>
            <h1>{data.title}</h1>
            <h2>{data.auther}</h2>
            <h2>{data.genre}</h2>
            <h3>{data.rating}</h3>
            <p>{data.desc}</p>
        </div>
            
            </div>}
      
    </div>
  )
}

export default Second
